commits.content:
server {
    listen 80;
    server_name localhost;

    root /var/www/html/public;
    index index.php index.html index.htm;

    # Buffer and size settings to prevent 414 Request-URI Too Large error
    client_max_body_size 10M;  # Maximum allowed request body size
    client_header_buffer_size 1k;  # Request header buffer size
    large_client_header_buffers 4 16k;  # Maximum header buffer size for large headers

    # Optional: Add timeouts to handle larger requests or slow client connections
    client_body_timeout 12s;
    client_header_timeout 12s;
    keepalive_timeout 15s;
    send_timeout 10s;

    # Serve static files and fallback to Laravel's index.php
    location / {
        try_files $uri $uri/ /index.php?$query_string;
        add_header Access-Control-Allow-Origin *;
        add_header Access-Control-Allow-Methods "GET, POST, OPTIONS";
        add_header Access-Control-Allow-Headers "Origin, Content-Type, Accept, Authorization";
    }

    # PHP-FPM handling through FastCGI
    location ~ \.php$ {
        include fastcgi_params;
        fastcgi_pass app:9000; # The service name 'app' from docker-compose.yml
        fastcgi_index index.php;
        fastcgi_param SCRIPT_FILENAME $document_root$fastcgi_script_name;

        # Optional: Add FastCGI buffer settings for handling large PHP requests
        fastcgi_buffers 16 16k;
        fastcgi_buffer_size 32k;
    }

    # Deny access to .htaccess files
    location ~ /\.ht {
        deny all;
    }
}
